UPDATE dbscript_string SET content_default="The bonelashers of the wastes are no laughing matter.  I hear they carry diseases!", broadcast_text_id=16978 WHERE entry=2000005688;
UPDATE dbscript_string SET content_default="Hmm... the readings on the gravimeteric distortions seem to have stabalized.  We did it!", broadcast_text_id=17020 WHERE entry=2000005260;
UPDATE dbscript_string SET content_default="According to these readings, we have nothing to fear.  At least nothing in the near future.  We'll need more data to be sure.", broadcast_text_id=17059 WHERE entry=2000005262;
UPDATE dbscript_string SET content_default="Hail, Gezhe!  Seventeen more orders have been placed in the Hara'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted.", broadcast_text_id=17184 WHERE entry=2000005362;
UPDATE dbscript_string SET content_default="Heraazi exports are on the rise, sir.  We've already bribed the appropriate taxation officers and contraband will begin to arrive within days.", broadcast_text_id=17186 WHERE entry=2000005363;
UPDATE dbscript_string SET content_default="As expected.  Very well, dismissed.", broadcast_text_id=17190 WHERE entry=2000005365;
UPDATE dbscript_string SET content_default="Nothing surprising there.  Come back with some real news, will you?", broadcast_text_id=17191 WHERE entry=2000005364;
UPDATE dbscript_string SET content_default="What manner of creature is that?  Looks like a purple goblin.", broadcast_text_id=17237 WHERE entry=2000005591;
UPDATE dbscript_string SET content_default="Do not burden A'dal with mundane questions, $r.  This being's will is all that keeps our enemies from crushing this city.", broadcast_text_id=17238 WHERE entry=2000000221;
UPDATE dbscript_string SET content_default="Brutish though they seem, these iron-stitched horrors are exceedingly brilliant engineers and forge-smiths.", broadcast_text_id=17405 WHERE entry=2000000741;
UPDATE dbscript_string SET content_default="The ship's not ready yet.  We still need to calibrate the fuse length to make sure that it doesn't burn out and leave you up there alone.", broadcast_text_id=17414 WHERE entry=2000005249;
UPDATE dbscript_string SET content_default="Essentially, it's going to be a long, long time till we are ready to launch.  Maybe you should just head back home to your family.", broadcast_text_id=17417 WHERE entry=2000005251;
UPDATE dbscript_string SET content_default="I can't. I'm not the man they think I am at home.  Besides, I didn't bring them out here.", broadcast_text_id=17419 WHERE entry=2000005252;
UPDATE dbscript_string SET content_default="Well, it's like I said; it's going to be a while before we get this ship ready for flight.  We'll keep you informed.", broadcast_text_id=17424 WHERE entry=2000005257;
UPDATE dbscript_string SET content_default="Hey, you there.  Want some candy?", broadcast_text_id=17486 WHERE entry=2000005052;
UPDATE dbscript_string SET content_default="What up son!  Wait....is anyone there?", broadcast_text_id=17488 WHERE entry=2000005055;
UPDATE dbscript_string SET content_default="If you think I'll betray my friend that easily, think again!  Face me, you scoundrel!", broadcast_text_id=17758 WHERE entry=2000000253;
UPDATE dbscript_string SET content_default="Speak with Wing Commander Brack, $n.  He'll outfit you with one of our armored wyvern destroyers.  And, good luck!", broadcast_text_id=17964 WHERE entry=2000000180;
UPDATE dbscript_string SET content_default="I thought this was going to be our chance to get away from it all?  Our vacation?", broadcast_text_id=18817 WHERE entry=2000005680;
UPDATE dbscript_string SET content_default="ACK!  That's the worst thing I've ever tasted!  I wouldn't let my ram drink that!", broadcast_text_id=18830 WHERE entry=2000000284;
UPDATE dbscript_string SET content_default="Hah!  The Thunderspike is mine.  Die!", broadcast_text_id=18979 WHERE entry=2000001123;
