UPDATE dbscript_string SET content_default="Keep up the good work.  Do well and you will be rewarded by the master.", broadcast_text_id=19153 WHERE entry=2000005470;
UPDATE dbscript_string SET content_default=" I think that we should teach a lesson to both the Horde and the Alliance.  One that they will not soon forget!", broadcast_text_id=19194 WHERE entry=2000001326;
