UPDATE dbscript_string SET content_default="This cell won't hold me for long.  I will have Illidan's head one way or another.", broadcast_text_id=19394 WHERE entry=2000005475;
UPDATE dbscript_string SET content_default="I've waited for this moment for years.  Illidan and his lapdogs will be destroyed!", broadcast_text_id=20743 WHERE entry=2000005527;
UPDATE dbscript_string SET content_default="Such... dark... energy!  How can this be possible?", broadcast_text_id=13397 WHERE entry=2000008487;
UPDATE dbscript_string SET content_default="Thank you, $n.  It looks like they're going to get along just fine.", broadcast_text_id=13666 WHERE entry=2000000181;

