UPDATE dbscript_string SET content_default="Ok, let's get started.", broadcast_text_id=1610 WHERE entry=2000000096;
UPDATE dbscript_string SET content_default="I believe the Forsaken are misleading the allies of the Horde.... wait... I feel so... dizzy...", broadcast_text_id=1859 WHERE entry=2000000121;
