UPDATE dbscript_string SET content_default="Spirits of Water, I give you praise, and I beg of you a favor. You have heard $N's pleas, and I trust $ghis:her; intent is noble. Please, will you aid us?", broadcast_text_id=6586 WHERE entry=2000000057;
UPDATE dbscript_string SET content_default="I have been expecting you, young one. Come closer - my eyes are not as sharp as they once were.", broadcast_text_id=17101 WHERE entry=2000003088;
UPDATE dbscript_string SET content_default="Yes, you move like my son. You have his broad shoulders - ahh, and your mother's fierce eyes.", broadcast_text_id=17120 WHERE entry=2000003089;
UPDATE dbscript_string SET content_default="There is no doubt - you are the heir of Durotan... my grandson. Draka told me she was with child before she and your father left our world, but I never dared dream that they would survive...", broadcast_text_id=17123 WHERE entry=2000003091;
UPDATE dbscript_string SET content_default="Go'el. You are Go'el, son of Durotan - rightful chieftain of the Frostwolves. This day, grandson - you are the great joy of my heart.", broadcast_text_id=17927 WHERE entry=2000003095;
UPDATE dbscript_string SET content_default="I see that you hold the Doomhammer. Have all of our greatest heroes fallen, grandson?", broadcast_text_id=17929 WHERE entry=2000003097;
UPDATE dbscript_string SET content_default="What of Grommash? What of Hellscream?", broadcast_text_id=17930 WHERE entry=2000003099;
UPDATE dbscript_string SET content_default="Blessed spirits! For twenty years, all we knew was that Grom was the first to drink from the cursed chalice and bring damnation down upon our people... It is the only truth his son, Garrosh has ever known.", broadcast_text_id=17129 WHERE entry=2000003101;
